import { Component,OnInit } from '@angular/core';
import { EmployeeserviceService } from '../employeeservice.service';

@Component({
  selector: 'app-employeelist',
  templateUrl: './employeelist.component.html',
  styleUrls: ['./employeelist.component.css']
})
export class EmployeelistComponent implements OnInit{
  constructor(public serve:EmployeeserviceService){}
  data:any;
  ngOnInit(): void {
    this.serve.fetchData().subscribe((users)=>{
      this.data = users
      console.log(users)
    })
  }

  gotoEmployeeForm(){

  }
}
<div class="container">
    <div class="row">
      <span class="text-center fs-4 py-5">
        <button routerLink="employeeform">
          <i class="bi bi-plus-square"></i> Add New Employee
        </button>
      </span>
      <table class="table">
        <thead>
          <tr>
            <th class="text-center">ID No</th>
            <th class="text-center">Name</th>
            <th class="text-center">Email</th> 
            <th class="text-center">Actions</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let user of data">
            <td class="text-center">
              <i class="bi bi-person-bounding-box custom-icon"></i> {{ user.id }}
            </td>
            <td class="text-center">{{ user.name }}</td>
            <td class="text-center">
              <a href="mailto:{{ user.email }}"><i class="bi bi-envelope-at custom-icon"></i> {{ user.email }}</a>
            </td>
            <td class="text-center">
              <button><i class="bi bi-pencil-square"></i></button>
              <button><i class="bi bi-trash3"></i></button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
